function openAddNewLanguage(link,page){var url=xliteConfig.script+'?target=language_select&widget=\\XLite\\View\\LanguagesModify\\AddLanguage';loadDialogByLink(link,url,{width:600,height:500});return false}
function openAddNewLabel(link,language){var url=xliteConfig.script+'?target=language_label&widget=\\XLite\\View\\LanguagesModify\\AddLabel';if(language)url+='&code='+language;loadDialogByLink(link,url,{width:600,height:600});return false}
function openEditLabelDialog(link,id,language){var url=xliteConfig.script+'?target=language_label&label_id='+id+'&widget=\\XLite\\View\\LanguagesModify\\EditLabel';if(language)url+='&code='+language;loadDialogByLink(link,url,{width:600});return false}