INSERT INTO `xc_config_translations` VALUES (453, 387, 'Site connection code', 'The connection code will appear here automatically once you upload store data to MailChimp. Alternatively, you can connect MailChimp subscription popup via Custom Website integration code. Add your site to the MailChimp account and paste here the connection code.', 'en');
