INSERT INTO `xc_config_translations` VALUES (488, 401, 'Режим обновления кэша', 'Кеш впервые генерируется при открытии страницы категории. При внесении изменений в данные атрибутов, тегов или товаров, кеш сбрасывается. Затем он генерируется заново, когда происходит открытие любой категории.', 'ru');
