INSERT INTO `xc_language_label_translations` VALUES (5442, 4736, '\"Bidirectional link\" means that the related product is not only linked to from the product on view, but also provides a link to that product. With the \"Bidirectional link\" option enabled, removing the link from one of the products will not remove the link in the opposite direction. To remove the relationship between the products completely, disable the \"Bidirectional link\" option first, then remove the link.', 'en');
INSERT INTO `xc_language_label_translations` VALUES (5443, 4736, '\"Двунаправленная связь\" значит, что не только просматриваемый товар ссылается на связанный, но и связанный ссылается на него. При включенной настройке \"Двунаправленная связь\", удаление ссылки с одного из товаров не приведет к удалению ссылки в противоположном направлении. Для полного удаления связи между товарами, вначале отключите настройку \"Двунаправленная связь\", затем удалите ссылку на связанный товар.', 'ru');
