INSERT INTO `xc_page_translations` VALUES (1, 1, '', 'Terms and conditions', 'Terms and conditions', '<p>Terms and conditions text</p><p>To replace this text with the actual Terms and conditions, go to the \"Content -> Pages\" section, open the \"Terms &amp; Conditions\" page and edit the \"Content\" field.</p>', '', '', 'en');
INSERT INTO `xc_page_translations` VALUES (2, 1, '', 'Условия использования', 'Условия использования', '<p>Чтобы разместить на этой странице Ваш собственный текст \"Условия использования\", перейдите в раздел \"Содержание -> Страницы\", откройте для редактирования страницу \"Условия использования\" и добавьте требуемый текст в поле \"Содержание\".</p>', '', '', 'ru');
INSERT INTO `xc_page_translations` VALUES (3, 1, '', '条款约定', '条款约定', '<p>内容描述</p><p>要替代该条款内的内容，请到&ldquo;内容-网页&ldquo;中进行设置，打开页面编辑并在这里编辑内容栏目。</p>', '', '', 'zh');
INSERT INTO `xc_page_translations` VALUES (4, 2, '', 'Shipping', 'Shipping teaser', '<p>This page is intended for your store\'s shipping policy.</p><p>Here you can provide general information about the available shipping options and rates, shipping turn-around times, size and weight limits for shipping, shipping restrictions, order tracking, and so on.</p><p></p><p>To replace this text with the actual delivery information, go to the \"Content -> Pages\" section, open the \"Shipping\" page and edit the \"Content\" field.</p>', '', '', 'en');
INSERT INTO `xc_page_translations` VALUES (5, 2, '', 'Доставка', 'Доставка', '<p>Чтобы разместить на этой странице Ваш собственный текст \"Доставка\", перейдите в раздел \"Содержание -> Страницы\", откройте для редактирования страницу \"Доставка\" и добавьте требуемый текст в поле \"Содержание\".</p>', '', '', 'ru');
INSERT INTO `xc_page_translations` VALUES (6, 2, '', '关于运输', '关于运输', '<p>关于运输的内容</p><p>要设置该页面的运输项目内容，请到&rdquo;内容-页面&rdquo;中打开关于运输页面进行修改。</p>', '', '', 'zh');
