INSERT INTO `xc_product_translations` VALUES (1, 1, '', 'Timberland 45th Anniversary', '', '', '', '', '', 'en');
INSERT INTO `xc_product_translations` VALUES (2, 2, '', 'High Top Trainers in Red, Black and White [Tags]', '<p><strong>Features:</strong></p><ul><li>Regular Fit</li><li>Leather upper with logo</li><li>Mesh lining</li><li>Foam padded tongue and collar</li><li>Ventilation holes</li><li>Abrasion-resistant rubber outsole</li><li>Leather upper, rubber outsole</li></ul>', '<p>This sneaker delivers heritage style with premium materials and responsive cushioning. The red, black and white uppers provide a bold and stylish take on the iconic silhouette. Featuring a high top style, a lace-up front fastening, a round toe and a well padded ankle collar and tongue, this shoe is an ideal companion for sports and day to day wear.<br><br><br></p>', '', 'This sneaker delivers heritage style with premium materials and responsive cushioning. The red, black and white uppers provide a bold and stylish take on the iconic silhouette. Featuring a high top style, a lace-up front fastening, a round toe and a well padded ankle collar and tongue, this shoe is an ideal companion for sports and day to day wear.', '', 'en');
INSERT INTO `xc_product_translations` VALUES (3, 3, '', 'Beige Sneakers', '<p>Lightweight and breathable canvas construction. Vulcanized rubber sole for improved traction. An unmistakable logo patch to complete your look. An ortholite insole cushions each step. Versatile, fashionable, and looking better every time you wear them. &nbsp;</p><p><strong>Features:</strong></p><ul><li>Hi top plimsolls&nbsp;</li><li>Lace-up front</li><li>Embroidered logo to side</li><li>Regular Fit</li><li>Outer: Cotton 100%</li><li>Sole: Rubber 100%</li><li>Lining: Cotton 100%</li></ul>', '<p>Beige cotton hi-top sneakers featuring a lace-up front fastening, a round toe, a branded ankle patch and a flat rubber sole.</p>', '', 'Beige cotton hi-top sneakers featuring a lace-up front fastening, a round toe, a branded ankle patch and a flat rubber sole.', '', 'en');
INSERT INTO `xc_product_translations` VALUES (4, 4, '', 'Platform Brogue-Trim Leather Oxfords in White', '<p>Take your style up a notch with these super fashionable platform oxfords. Wear with leggings and an oversized shirt or off-the-shoulder blouse for an on-trend look. Comfortable platform with a non-skid, much lighter than you imagine, manmade sole.</p><p><strong>Features:</strong></p><ul><li>Leather oxford with brogue detail</li><li>Espadrille-inspired platform</li><li>1.25 inch heel</li><li>Round Toe</li><li>Adjustable lace-up closure</li><li>Water resistant</li><li>Leather upper</li><li>Antiskid manmade sole</li></ul>', '<p>A mixed-media oxford steals the scene with an espadrille-trimmed platform sole and dramatic brogue detailing on the burnished leather upper.</p>', '', 'A mixed-media oxford steals the scene with an espadrille-trimmed platform sole and dramatic brogue detailing on the burnished leather upper.', '', 'en');
INSERT INTO `xc_product_translations` VALUES (5, 5, '', 'Grey Knitted Trainers', '<p>This shoe hits the sweet spot of effortless comfort and universal style. Knitted from a lightweight sports-mesh that hugs the foot for a snug fit, with an easy-entry collar shaped to perfection cushioned with the lightweight EVA midsole and finished with a flexible rubber outsole for excellent grip in any conditions, this shoe has delighted many a runner who just want a shoe that feels indulgent on their foot.&nbsp;</p><p><strong>Features:</strong></p><ul><li>Regular Fit</li><li>Knit textile upper</li><li>EVA midsole</li><li>Flexible rubber outsole</li></ul>', '<p>Designed with your comfort in mind, a pair of knitted trainers is a go to for any day! &nbsp;</p>', '', 'Designed with your comfort in mind, a pair of knitted trainers is a go to for any day! &nbsp;', '', 'en');
INSERT INTO `xc_product_translations` VALUES (6, 6, '', 'Lace-Up Boots in Light Brown', '<p>Waterproof, rugged, built for comfort and traction, these boots can go all day and will only look better with age.&nbsp;</p><p><strong>Features:</strong></p><ul><li>Premium leather upper</li><li>Seam-sealed waterproof construction</li><li>400 gram synthetic insulation</li><li>Padded collar</li><li>Leather lining</li><li>Flexible forefoot construction</li><li>Anti-fatigue midsole and footbed</li><li>Rubber lug outsole</li></ul>', '<p>Insulated waterproof boots for your next adventure.</p>', '', 'Insulated waterproof boots for your next adventure.', '', 'en');
INSERT INTO `xc_product_translations` VALUES (7, 7, '', 'Boyfriend T-Shirt with Chest Logo', '<p>A wardrobe must-have, this crewneck tee features a comfortable cotton blend and a straight hem for easy, everyday wear. Think baggy and casual. Wear it again and again.</p>', '', '', 'A wardrobe must-have, this crewneck tee features a comfortable cotton blend and a straight hem for easy, everyday wear. Think baggy and casual. Wear it again and again.', '', 'en');
INSERT INTO `xc_product_translations` VALUES (8, 8, '', 'Pink Sneakers with White Soles', '<p>Low-top silhouette with a durable canvas upper. &nbsp;Lace-up front with flat cotton laces. Round toe. Lightly padded tongue and collar. Vulcanized sole with high-wall foxings. Premium cushioned flexible sole. &nbsp;</p><p><strong>Features:</strong></p><ul><li>Regular Fit</li><li>Lace-up construction</li><li>Textile upper</li><li>Flexible rubber outsole</li></ul>', '<p>Timeless low top sneakers in pink. &nbsp;</p>', '', 'Timeless low top sneakers in pink. &nbsp;', '', 'en');
INSERT INTO `xc_product_translations` VALUES (9, 9, '', 'White Lace-Up Mesh Trainers', '<p>This pair is perfect for a variety of workouts in and out of the gym. School, play, travel, jogging, running - you name it! A great gift for a sporty girl.</p><p><strong>Features:</strong></p><ul><li>Lightweight</li><li>Mesh textile upper</li><li>Flexible shock absorbing EVA midsole</li><li>Rugged and durable outsole for long lasting traction</li></ul>', '<p>Breathable soft knit mesh sneakers for sports and more.</p>', '', 'Breathable soft knit mesh sneakers for sports and more.', '', 'en');
INSERT INTO `xc_product_translations` VALUES (10, 10, '', 'Caged Cutout Sandals in Gold', '<p>These sandals feature an exquisite cut-out design on the upper and high heels to showcase your unique sense of style. This time made with a gold finish to give this piece a sleek and refined look. &nbsp;</p><p><strong>Features:</strong></p><ul><li>Vegan-friendly leather with geometric cutout design</li><li>Adjustable buckle closure at ankle</li><li>Open toe</li><li>Stiletto heel</li><li>Heel height: 3 <sup>1</sup>&frasl;<sub>2</sub> in</li></ul>', '<p>More feminine, more refined, more fun!</p>', '', 'More feminine, more refined, more fun!', '', 'en');
INSERT INTO `xc_product_translations` VALUES (11, 11, '', 'Green Flat Slingback Sandals', '<p>Quality made with premium elastic fabric, these strappy open toe sandals are a HOT TREND! Designed for all day comfort, they keep you moving while looking effortlessly stylish.&nbsp;</p><p><strong>Features:</strong></p><ul><li>Open toe sandal</li><li>Medium Width</li><li>Lightweight&nbsp;</li><li>Textile and synthetic leather upper</li><li>Soft rubber sole</li></ul>', '<p>With the rubber sole and wide elastic straps hugging your foot, these open toe sandals are made for walking.</p>', '', 'With the rubber sole and wide elastic straps hugging your foot, these open toe sandals are made for walking.', '', 'en');
INSERT INTO `xc_product_translations` VALUES (12, 12, '', 'Waist Tie Dress in Blue and White Stripe', '<p>Crafted in a lightweight blend of linen and cotton and finished with muted stripes, this dress is an everyday piece that is perfect for long, sunny days.</p>', '', '', 'Crafted in a lightweight blend of linen and cotton and finished with muted stripes, this dress is an everyday piece that is perfect for long, sunny days.', '', 'en');
INSERT INTO `xc_product_translations` VALUES (13, 13, '', 'Ultimate T-Shirt with Crew Neck in Red', '<p><br></p><ul><li>Crew neck</li><li>Short sleeves</li><li>Regular fit</li><li>100% Cotton</li></ul>', '<p>Another stylish addition your wardrobe. This crew-neck tee was designed specifically for a fashionable, comfortable, feminine look.</p>', '', 'Another stylish addition your wardrobe. This crew-neck tee was designed specifically for a fashionable, comfortable, feminine look.', '', 'en');
INSERT INTO `xc_product_translations` VALUES (14, 14, '', 'Multicolored Dress with Balloon Sleeves [Options & Attributes]', '<ul><li>Round neck</li><li>Dropped shoulders</li><li>Balloon sleeves</li><li>Don&#39;t worry, you won&rsquo;t float away</li><li>Fitted cuffs&nbsp;</li><li>Relaxed fit</li><li>Loose but not oversized</li><li>100% Polyester</li></ul>', '<p>Cute, right? Bright floral print in relaxed fit. Throw on a black leather jacket and wrap a belt around the waist &nbsp;and you&#39;re ready for a night out.</p>', '', 'Cute, right? Bright floral print in relaxed fit. Throw on a black leather jacket and wrap a belt around the waist &nbsp;and you&#39;re ready for a night out.', '', 'en');
INSERT INTO `xc_product_translations` VALUES (15, 15, '', 'Tall Pleated Midi Skirt in Check', '<ul><li>High rise</li><li>Just like your standards</li><li>Pleated design</li><li>Regular cut</li><li>Fits you just right</li><li>Lightweight knit. The kind that doesn&#39;t stretch</li><li>100% Polyester</li></ul>', '<p>Suitable for spring, summer and autumn. You can&#39;t miss such a charming skirt in your wardrobe.<br>Besides, you can dress it up with heels, or dress it down with sandals and t-shirt for a more casual look.</p><p><br></p><p><br></p>', '', 'Suitable for spring, summer and autumn. You can&#39;t miss such a charming skirt in your wardrobe. Besides, you can dress it up with heels, or dress it down with sandals and t-shirt for a more casual look.', '', 'en');
INSERT INTO `xc_product_translations` VALUES (16, 16, '', 'Sleeved Mini Dress in Color Block', '<ul><li>Color-block design</li><li>55% Polyester, 45% Cotton</li><li>Round neck</li><li>Shift design</li><li>Regular cut</li><li>Fits you just right</li></ul>', '<p>An easy way to wear more color - 3-in-1 mini dress in modern color block design. Sleeves are non-detachable and can be completely unzipped to show that extra skin.&nbsp;</p>', '', 'An easy way to wear more color - 3-in-1 mini dress in modern color block design. Sleeves are non-detachable and can be completely unzipped to show that extra skin.&nbsp;', '', 'en');
INSERT INTO `xc_product_translations` VALUES (17, 17, '', 'Tall Slim Fit T-Shirt in White', '<ul><li>Throw on and go</li><li>Crew neck</li><li>Short sleeves</li><li>Plain design</li><li>Slim fit</li><li>100% Cotton.</li></ul>', '<p>This classic tee features all-cotton construction and a tag-free crew neckline for comfort. Affordable, high-quality, and long-lasting everyday clothing you can rely on.</p>', '', 'This classic tee features all-cotton construction and a tag-free crew neckline for comfort. Affordable, high-quality, and long-lasting everyday clothing you can rely on.', '', 'en');
