INSERT INTO `xc_product_translations` VALUES (22, 22, '', 'Mesh Strap Watch in Gold', '<ul><li>Stainless-steel mesh strap</li><li>Gold-tone case</li><li>Three hand movement</li><li>Dash indices</li><li>Single crown to side</li><li>Presented in a branded gift-box</li></ul>', '<p>Elegant, flawlessly feminine and easy to read with a large polished goldtone dial. Let your wrist do the talking.</p>', '', 'Elegant, flawlessly feminine and easy to read with a large polished goldtone dial. Let your wrist do the talking.', '', 'en');
INSERT INTO `xc_product_translations` VALUES (23, 23, '', 'Square Glasses in Black with Clear Lens', '<p>Frame: 100% Plastic, Lens: 100% Plastic.</p>', '<p>Whether you are looking for reading glasses for the office or home. These reading glasses will provide you with the best service and the premium protection you are looking for.</p><p>Square frames. Moulded nose pads for added comfort. Clear lenses. Tapered arms with curved temple tips for a secure fit.&nbsp;</p><p>Cloth sunglasses pouch can be used as a lens wipe.</p><p><br></p>', '', 'Whether you are looking for reading glasses for the office or home. These reading glasses will provide you with the best service and the premium protection you are looking for. Square frames. Moulded nose pads for added comfort. Clear lenses. Tapered arms with curved temple tips for a secure fit.&nbsp; Cloth sunglasses pouch can be used as a lens wipe.', '', 'en');
