INSERT INTO `vs_config_translations` VALUES (449, 384, 'Subscription select element type', 'This option controls the way your customers select a subscription. Select box allows subscription to only one mail list while check box allows subscription to multiple lists. For Checkout page always stays checkbox.', 'en');
