INSERT INTO `vs_config_translations` VALUES (451, 386, 'Type of discount coupons that should be uploaded to Mailchimp', 'The Mailchimp promo rules are:<br> - Code<br> - Discount amount<br> - Active from<br> - Active till <br>Discount coupons configured using any rules outside this rule set will be treated as not matching the Mailchimp rules.', 'en');
