INSERT INTO `vs_language_label_translations` VALUES (5442, 4736, '\"Bidirectional link\" means that the related product is not only linked to from the product on view, but also provides a link to that product. With the \"Bidirectional link\" option enabled, removing the link from one of the products will not remove the link in the opposite direction. To remove the relationship between the products completely, disable the \"Bidirectional link\" option first, then remove the link.', 'en');
