INSERT INTO `vs_notification_translations` VALUES (1, 'failed_admin_login', 'Failed admin login', 'The notification is sent to the email address of the admin user for whose account failed login attempts have been detected several times in a row. A copy of the message is also sent to the store’s super admin user.', '', '', 'Knock-knock. Forgot your password?', '<h3 dir=\"ltr\">Hey there,</h3><p dir=\"ltr\">Someone is trying to break into your admin account.&nbsp;</p><p dir=\"ltr\">Is that you? Ok, then. You can reset the forgotten password <a href=\"%reset_link%\">here</a>.</p><p dir=\"ltr\">If you didn&rsquo;t mean to reset your password, then you can safely ignore this message. Your password will not change. Just make sure it&rsquo;s secure enough. &nbsp;</p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (2, 'failed_transaction', 'Payment declined', 'This notification is sent to the administrator if payment transaction was failed', '', '', 'Payment was declined', '<p dir=\"ltr\">One of your customers was about to make a purchase, but your payment processor failed to charge his card for some reason.&nbsp;</p><p dir=\"ltr\">%dynamic_message%</p><p dir=\"ltr\">Wonder why it may have happened? It&rsquo;s likely that your customer entered a wrong card number, password or CVC, had no money on his card, closed his browser by mistake or just fleeted away.</p><p dir=\"ltr\">It may be a good idea to contact him and offer your help.</p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (3, 'low_limit_warning', 'Low limit warning', 'This message is used to notify the administrator when the stock level of a product reaches the low stock limit as specified in the product details.', '', '', 'You are running out of %product_name%', '<h3 dir=\"ltr\">Heads-up boss, </h3><p dir=\"ltr\">I see that some of your products are about to run out of stock.&nbsp;</p><p dir=\"ltr\">%dynamic_message%</p><p dir=\"ltr\">According to our stats you&rsquo;ll sell the last item by %latest_sale_date%. So it&rsquo;s high time to replenish them. You are not going to sell fresh air, are you?</p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (4, 'backorder_created', 'Backordered item in the order', 'This notification is sent to the administrator if one or more products in a customer\'s order are out of stock at the time of order placement.', '', '', 'A new backorder %backordered_item_names%', '<p dir=\"ltr\">A customer has just bought one or more items that are out of stock at the moment. It is time to replenish your supplies.&nbsp;</p><p dir=\"ltr\"><a href=\"%order_link%\">Check the order #%order_number%</a></p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (5, 'order_changed', 'Order changed', 'This notification is sent when the status of an order is updated - in the event that no other type of notification is sent.', 'Order #%order_number% is changed', '<p dir=\"ltr\">As previously agreed, we&rsquo;ve changed your order. Here is a new invoice for you, fresh and updated. Check it out to make sure that nothing is missing.</p>', 'Order #%order_number% is changed', '<p dir=\"ltr\">I&rsquo;ve saved all your order changes just in case you might need them in future. Please, take a look and make sure that everything is ok.</p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (6, 'order_canceled', 'Order canceled', 'This notification is sent when an order gets the status Canceled.', 'Order #%order_number% is canceled', '<h3 dir=\"ltr\">Hi %recipient_name%,</h3><p dir=\"ltr\">We had to cancel your order. If that was you who initiated the cancellation, please tell us where we failed and let us at least try to make up for our faults. For any questions and feedback, just hit &#39;Reply&#39;.</p>', 'Order #%order_number% is canceled', '<p dir=\"ltr\">I&rsquo;m sorry to see that you had to cancel the order. Hope your customer will change his mind and end up buying from you soon. I&rsquo;ll keep my fingers crossed.</p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (7, 'order_created', 'Order created', 'This notification is sent when a new order is created.', 'Order #%order_number% is received', '<p dir=\"ltr\">Thank you for shopping with us!&nbsp;</p><p dir=\"ltr\">This is just a quick note to let you know that we&rsquo;ve received your order and we will process it as soon as we can. We will keep you updated on the status of your order.&nbsp;</p>', 'You’ve got a new order #%order_number% for %order_total%', '<p dir=\"ltr\">Your wallet is going to get heavier very soon. Go ahead, show your customer you care and process the order ASAP.</p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (8, 'order_failed', 'Order declined', 'This notification is sent when an order gets the status Declined.', 'Order #%order_number% is declined', '<h3 dir=\"ltr\">Hi %recipient_name%,</h3><p dir=\"ltr\">Something went wrong and %payment_method_name% failed to charge your credit card.&nbsp;</p><p dir=\"ltr\">If everything seems alright on your side and you are eager to proceed with the purchase, please reply back. We&rsquo;ll try to find out what&rsquo;s up.</p>', 'Order #%order_number% is declined', '<p dir=\"ltr\">I see that your order was declined by %payment_method_name%. It may be a good idea to contact your customer and offer your help.</p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (9, 'order_processed', 'Order processed', 'This notification is sent when an order gets the status Paid.', 'Order #%order_number% is paid (yay!)', '<h2 dir=\"ltr\">Your order was paid successfully, %recipient_name%!</h2><h3 dir=\"ltr\">And we are just as excited as you are</h3><p dir=\"ltr\"><br>Take a look below for all the confirmation details you&rsquo;ll need. We will be back in a moment to let you know that your item is on its way, unless you decide to pick it up yourself.&nbsp;</p>', 'Order #%order_number% is paid', '<p dir=\"ltr\">Your wallet has got a little heavier. Way to go!</p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (10, 'order_shipped', 'Order shipped', 'This notification is sent to the customer when an order gets the status Shipped.', 'Order #%order_number% is shipped', '<h2 dir=\"ltr\">Bingo %recipient_name%!</h2><h3 dir=\"ltr\">Your item is on its way to your doorstep</h3><p dir=\"ltr\"><br>We&rsquo;ve packed your order and sent it off on a journey to your place. The delivery shouldn&rsquo;t take much time as we turbo-charged our van, so it gets to you extra fast.</p><p dir=\"ltr\">%dynamic_message%</p><p dir=\"ltr\">If you have any questions feel free to get in touch. We are always here and super happy to help you.</p>', '', '', 'en');
INSERT INTO `vs_notification_translations` VALUES (11, 'order_tracking_information', 'Order tracking information', 'This notification is sent to the customer when an administrator clicks the \"Send tracking information to customer\" button.', 'Order #%order_number% tracking information', '<p dir=\"ltr\">We&rsquo;ve sent your parcel off on a journey to your porch via %shipping_method_name%.&nbsp;</p><p dir=\"ltr\">Below you&rsquo;ll find shipping details and a handy tracking link you can use to follow your order&rsquo;s trip to your door.</p><p dir=\"ltr\">%dynamic_message%</p><p dir=\"ltr\">Don&#39;t worry if the order tracking doesn&#39;t work right away. It may take some time to have this information available.</p>', '', '', 'en');
INSERT INTO `vs_notification_translations` VALUES (12, 'order_waiting_for_approve', 'Order awaiting for approval', 'This notification is sent to the customer when an order gets the status Waiting for approve.', 'Order #%order_number% is waiting for approval', '<h3 dir=\"ltr\">Hi %recipient_name%,</h3><p dir=\"ltr\">Thank you for shopping on %company_name%!&nbsp;</p><p dir=\"ltr\">Our financial team needs some time to manually review your order.</p><p dir=\"ltr\">Please wait for a couple of hours and take another look at your inbox. There should be one more email ready and waiting.</p>', '', '', 'en');
INSERT INTO `vs_notification_translations` VALUES (13, 'profile_created', 'User profile created', 'This notification is sent when a new customer profile is created.', 'Welcome to %company_name%!', '<h3 dir=\"ltr\">Welcome to %company_name%!</h3><p dir=\"ltr\">The world of online shopping is now at your fingertips</p><p dir=\"ltr\">We&rsquo;ve created an account for you. There you will find:</p><ul><li dir=\"ltr\">all your previous (yes, we&rsquo;ve saved them all!) and future orders;</li><li dir=\"ltr\">addresses in case you need to get things delivered to different places;</li><li dir=\"ltr\">profile details, where you can edit your login and password.</li><li dir=\"ltr\">messages you&rsquo;ve ever sent or received from us;</li></ul><p>Sign in to <a href=\"%sign_in_url%\">your account</a> to start using all these features. And if you have any questions, just reply back.&nbsp;</p>', 'Great! You\'ve got a new customer', '<p>One more customer has just signed up: %customer_email%. Looks like your business is growing :)</p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (14, 'profile_deleted', 'User profile deleted', 'This notification is sent to the administrator when a customer profile is deleted.', '', '', 'Oops.. one customer less', '<p dir=\"ltr\">One of your customers has just silently deleted his profile: %deleted_profile%</p><p dir=\"ltr\">Make sure you no longer keep his personal data anywhere in your database</p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (15, 'recover_password_request', 'Password reset request', 'This notification is sent to the user when a password reset is requested for their account.', 'Password reset request', '<p dir=\"ltr\">We&rsquo;ve received a request to reset your forgotten password.</p><p dir=\"ltr\">If you didn&rsquo;t ask for it, good, there&rsquo;s nothing else you need to do. Just safely ignore this message.&nbsp;</p><p>Otherwise, please <a href=\"%recover_url%\">click here to change your password</a> right away.</p>', 'Password reset request', '<p dir=\"ltr\">I&rsquo;ve received a request to reset your forgotten password.</p><p dir=\"ltr\">If you didn&rsquo;t make this request, good, there&rsquo;s nothing else you need to do. Just safely ignore this message.&nbsp;</p><p>Otherwise, please <a href=\"%recover_url%\">click here to change your password</a> right away.</p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (16, 'register_anonymous', 'User account created for guest buyer based on order info', 'This notification is sent to the customer when a customer profile is created for them by the store administrator based on a previously created order.', 'Welcome to %company_name%!', '<h2 dir=\"ltr\">Thank you for shopping with us, %recipient_name%!&nbsp;</h2><p dir=\"ltr\"><br>We noticed that you&rsquo;ve made a purchase using our guest checkout. It looks like you are too pressed for time to create an account in our store. No problem. We did it for you:</p><p dir=\"ltr\">Email: <strong>%customer_email%</strong><br>Password: <strong>%customer_password%</strong></p><p dir=\"ltr\">There you will find all your previous (yes, we&rsquo;ve saved them all!) and future orders, addresses, messages, and profile details, where you can change your password at any time.</p><p dir=\"ltr\">If you need any help, just reply back.&nbsp;</p>', '', '', 'en');
INSERT INTO `vs_notification_translations` VALUES (17, 'access_link', 'Access link re-sent', 'This notification is sent to the guest buyer when they request a new link for access to information about their order after an access link issued to them previously has expired.', 'New access link', '', '', '', 'en');
INSERT INTO `vs_notification_translations` VALUES (18, 'modules/CDev/ContactUs/message', 'Contact us form submitted (Contact us module)', 'This notification is sent to the administrator when someone submits a request via the \"Contact us\" form.', '', '', 'New message: %message_subject%', '<h3 dir=\"ltr\">Hey boss, </h3><p dir=\"ltr\">%message_author% has sent you a message through the &ldquo;Contact us&rdquo; form and is waiting for your answer. Come on, reply to this email and drop him a line or two.</p><blockquote><p dir=\"ltr\">%message%</p></blockquote>', 'en');
INSERT INTO `vs_notification_translations` VALUES (19, 'modules/CDev/ContactUs/message', 'Заполнена форма для связи с администрацией магазина (модуль Contact us)', 'Данное сообщение используется для оповещения администратора в случае поступления в адрес магазина обращения через форму для связи с администрацией.', '', '', 'Поступило обращение через форму для связи с администрацией магазина', '', 'ru');
INSERT INTO `vs_notification_translations` VALUES (20, 'modules/CDev/Egoods/egoods_links', 'E-goods download links (E-goods module)', 'This notification is sent to the customer when their order containing egoods gets the status Paid.', 'Download your e-goods', '<h2 dir=\"ltr\">Your order is ready, %recipient_name%!</h2><h3 dir=\"ltr\">And we are just as excited as you are<br><br></h3><p dir=\"ltr\">Take a look below for the files you&rsquo;ve just purchased:</p><p dir=\"ltr\">%dynamic_message%</p><p dir=\"ltr\">And if something is not working, feel free to contact us.</p>', '', '', 'en');
INSERT INTO `vs_notification_translations` VALUES (21, 'modules/CDev/Egoods/egoods_links', 'Ссылки для скачивания файлов (модуль E-goods)', 'Данное сообщение отправляется покупателю, когда размещенный им заказ, содержащий электронные товары, получает статус \"Оплачен\".', 'Ваши ссылки для скачивания файлов', 'Благодарим Вас за своевременную оплату заказа. Файлы приобретенных Вами товаров могут быть загружены по следующим ссылкам:', '', '', 'ru');
INSERT INTO `vs_notification_translations` VALUES (22, 'modules/XC/CanadaPost/return_approved', 'Return request approved (Canada Post module)', 'This notification is sent to the customer if their request to return a product via Canada Post is approved by the administrator.', 'Order #%order_number%: Return request approved', '<p dir=\"ltr\">It&rsquo;s a pity to know that our products didn&rsquo;t sit well with you for some reason.&nbsp;</p><p dir=\"ltr\">%dynamic_message%</p><p dir=\"ltr\">Anyway, we are ready to take the items back for a full refund. Please, <a href=\"https://www.canadapost.ca/web/en/kb/details.page?article=how_do_i_return_a_re&cattype=kb&cat=atthepostoffice&subcat=services\">ship the package back to our office</a> using the parcel service that best meets your needs.&nbsp;</p><p dir=\"ltr\">Feel free to contact us if you have any questions.</p>', '', '', 'en');
INSERT INTO `vs_notification_translations` VALUES (23, 'modules/XC/CanadaPost/return_approved', 'Запрос на возврат товара одобрен (модуль Canada Post)', 'Данное сообщение отправляется покупателю в случае, если его запрос на возврат товара с пересылкой службой Canada Post получает одобрение администратора.', 'Ваш запрос на возврат товара одобрен', 'Ваш запрос на возврат товара одобрен', '', '', 'ru');
INSERT INTO `vs_notification_translations` VALUES (24, 'modules/XC/CanadaPost/return_rejected', 'Return request rejected (Canada Post module)', 'This notification is sent to the customer if their request to return a product via Canada Post is rejected by the administrator.', 'Order #%order_number%: Return request rejected', '<h3 dir=\"ltr\">Hello %recipient_name%,</h3><p dir=\"ltr\">We are awfully sorry, but we cannot take your products back and make a refund according to our money back policy.</p><p dir=\"ltr\">%dynamic_message%</p><p dir=\"ltr\">Please contact us if you have any questions.</p>', '', '', 'en');
INSERT INTO `vs_notification_translations` VALUES (25, 'modules/XC/CanadaPost/return_rejected', 'Запрос на возврат товара отклонен (модуль Canada Post)', 'Данное сообщение отправляется покупателю в случае, если его запрос на возврат товара с пересылкой службой Canada Post отклоняется администратором.', 'Ваш запрос на возврат товара отклонен', 'Ваш запрос на возврат товара отклонен', '', '', 'ru');
INSERT INTO `vs_notification_translations` VALUES (26, 'modules/XC/Reviews/new_review', 'New product review submitted - (Product Reviews module)', 'This message is used to notify the administrator when a customer submits a new product review.', '', '', 'New review for ‘%product_name%’', '<p dir=\"ltr\">%author_name% has just rated your &quot;%product_name%&quot; and left a review for you.</p><p dir=\"ltr\">%dynamic_message%</p><blockquote><p dir=\"ltr\">%review%</p></blockquote><p dir=\"ltr\">Follow <a href=\"%product_link%\">this link</a> to approve or remove this review. And don&rsquo;t forget to reply to your customer &mdash; we all love to be heard ;)</p>', 'en');
INSERT INTO `vs_notification_translations` VALUES (27, 'modules/XC/Reviews/new_review', 'Получен новый отзыв о товаре - (модуль Product Reviews)', 'Данное сообщение используется для уведомления администратора о том, что клиент оставил отзыв о каком-либо товаре.', '', '', 'Получен новый отзыв о товаре', '', 'ru');
INSERT INTO `vs_notification_translations` VALUES (28, 'modules/XC/Reviews/review_key', 'Request to review purchased products - (Product Reviews module)', 'This message is used to invite customers to leave feedback on the products they have purchased.', 'Order #%order_number%: Please, review', '<p>Thank you for purchasing our products!</p><p>%dynamic_message%</p><p>We hope you love them. And if you really do, please take a minute to review your order to let others know that we care about delivering the best quality.</p>', '', '', 'en');
INSERT INTO `vs_notification_translations` VALUES (29, 'modules/XC/Reviews/review_key', 'Приглашение оставить отзыв на купленные товары - (модуль Product Reviews)', 'Данное сообщение используется для отправки покупателю приглашения оставить отзыв о купленных им товарах.', 'Пожалуйста, оставьте отзыв о купленных товарах', '', '', '', 'ru');
INSERT INTO `vs_notification_translations` VALUES (30, 'modules/XC/VendorMessages/notification', 'Order message (Order messages module)', 'This message will be sent to user who involved to the conversation when a new message appears in the communication thread regarding an order', 'Order #%order_number%: new message', '<p dir=\"ltr\">I see there&rsquo;s a new message in your account. It&rsquo;s about the order <a href=\"%order_link%\">#%order_number%</a>, and it is waiting for your reply.&nbsp;</p><blockquote><p dir=\"ltr\">%message%</p></blockquote>', '', '', 'en');
INSERT INTO `vs_notification_translations` VALUES (31, 'modules/XC/VendorMessages/notification', 'Новое сообщение по заказу (модуль Order messages)', 'Это уведомление будет отправлено покупателю, когда в контексте сделанного им заказа появится новое сообщение', 'Новое сообщение по заказу №%order_number%', '%company_link%\n<p>\nВы получили новое сообщение по заказу %order_messages_link%:\n</p>\n<p>\n%message%\n</p>\n', '', '', 'ru');
INSERT INTO `vs_notification_translations` VALUES (32, 'modules/XC/VendorMessages/new_message_notification', 'New message (Order messages module)', 'This message will be sent to customer when a new message appears in the conversation', 'New message in conversation', '<p>You&#39;ve got a new message in %conversation_link%</p><blockquote><p>%message%</p></blockquote>', '', '', 'en');
