INSERT INTO `vs_payment_method_translations` VALUES (16, 10, 'PayPal Payflow Link', 'Credit/Debit card or PayPal', '', '', 'Accept Visa, MasterCard®, American Express, Discover and PayPal payments on your site by connecting a PayPal-hosted checkout page with your own internet merchant account. Customize the page to look like the rest of your site and make sure customer transactions are secure.', '', 'en');
INSERT INTO `vs_payment_method_translations` VALUES (17, 11, 'PayPal Partner Hosted with PCI Compliance', 'Credit/Debit card', '', '', 'PCI compliant payments on your site with Visa, MasterCard, American Express and Discover - without PayPal-hosted pages. Maintain full control over your customers\' checkout experience by providing a credit card form that will post your customers\' payment details from your site\'s native checkout page directly to the Gateway server.', '', 'en');
INSERT INTO `vs_payment_method_translations` VALUES (18, 12, 'PayPal Payments Advanced', 'Credit/Debit card or PayPal', '', '', 'Accept Visa, MasterCard®, American Express, Discover and PayPal payments securely. With this all-in-one option, customers stay on your site for the entire checkout process. It simplifies PCI compliance and opens the door to more than 100 million active PayPal customers. Requires credit approval (2-3 business days).', '', 'en');
INSERT INTO `vs_payment_method_translations` VALUES (19, 13, 'PayPal Payments Standard', 'PayPal', '', '', 'Add a PayPal payment button to your site to accept Visa, MasterCard®, American Express, Discover and PayPal payments securely. When your customers check out, they are redirected to PayPal to pay, then return to your site after they are finished.', '', 'en');
INSERT INTO `vs_payment_method_translations` VALUES (20, 14, 'PayPal Express Checkout', 'PayPal', '', '', 'Create a seamless checkout experience and increase your conversion rate by opening the door to over 100 million active PayPal customers who look for and use this fast, easy, and secure way to pay.', '', 'en');
INSERT INTO `vs_payment_method_translations` VALUES (21, 15, 'PayPal Credit', 'PayPal Credit', '', '', '', '', 'en');
INSERT INTO `vs_payment_method_translations` VALUES (22, 16, 'PayPal Adaptive payments (legacy)', 'PayPal', '', '', 'This method provides automatic transfer of payments to vendor users (the type of users enabled by the Multi-vendor module). The method may not be activated unless the module <a href=\'admin.php?target=addons_list_marketplace&moduleName=XC\\MultiVendor\' target=\'_blank\'>Multi-vendor</a> is installed and enabled. To use this method, store administrator is required to have an Application ID for PayPal Adaptive Payments API.', '', 'en');
INSERT INTO `vs_payment_method_translations` VALUES (23, 17, 'PayPal for Marketplaces (beta)', 'PayPal', '', '', 'PayPal for Marketplaces is a comprehensive payments solution for marketplaces, crowdfunding platforms, and other environments where people buy and sell goods and services or raise money. The solution is ideal if you run a multi-party commerce platform and want a flexible, end-to-end solution for processing payments.', '', 'en');
INSERT INTO `vs_payment_method_translations` VALUES (24, 17, 'PayPal for Marketplaces (beta)', 'PayPal', '', '', 'PayPal for Marketplaces - это комплексное решение по приёму платежей для торговых площадок, платформ для сбора средств под проект (\"краудфандинг\") и других сфер, где осуществляется продажа и покупка товаров и услуг, либо сбор средств. Данное решение идеально подойдет для платформ, осуществляющих коммерческую деятельность от лица нескольких участников, и представляет собой комплексное и гибкое решение для обработки платежей на всех этапах этой деятельности.', '', 'ru');
