INSERT INTO `vs_product_translations` VALUES (1, 1, '', 'Timberland 45th Anniversary', '', '', '', '', '', 'en');
INSERT INTO `vs_product_translations` VALUES (2, 2, '', 'High Top Trainers in Red, Black and White [Tags]', '<p><strong>Features:</strong></p><ul><li>Regular Fit</li><li>Leather upper with logo</li><li>Mesh lining</li><li>Foam padded tongue and collar</li><li>Ventilation holes</li><li>Abrasion-resistant rubber outsole</li><li>Leather upper, rubber outsole</li></ul>', '<p>This sneaker delivers heritage style with premium materials and responsive cushioning. The red, black and white uppers provide a bold and stylish take on the iconic silhouette. Featuring a high top style, a lace-up front fastening, a round toe and a well padded ankle collar and tongue, this shoe is an ideal companion for sports and day to day wear.<br><br><br></p>', '', 'This sneaker delivers heritage style with premium materials and responsive cushioning. The red, black and white uppers provide a bold and stylish take on the iconic silhouette. Featuring a high top style, a lace-up front fastening, a round toe and a well padded ankle collar and tongue, this shoe is an ideal companion for sports and day to day wear.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (3, 3, '', 'Beige Sneakers', '<p>Lightweight and breathable canvas construction. Vulcanized rubber sole for improved traction. An unmistakable logo patch to complete your look. An ortholite insole cushions each step. Versatile, fashionable, and looking better every time you wear them. &nbsp;</p><p><strong>Features:</strong></p><ul><li>Hi top plimsolls&nbsp;</li><li>Lace-up front</li><li>Embroidered logo to side</li><li>Regular Fit</li><li>Outer: Cotton 100%</li><li>Sole: Rubber 100%</li><li>Lining: Cotton 100%</li></ul>', '<p>Beige cotton hi-top sneakers featuring a lace-up front fastening, a round toe, a branded ankle patch and a flat rubber sole.</p>', '', 'Beige cotton hi-top sneakers featuring a lace-up front fastening, a round toe, a branded ankle patch and a flat rubber sole.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (4, 4, '', 'Platform Brogue-Trim Leather Oxfords in White', '<p>Take your style up a notch with these super fashionable platform oxfords. Wear with leggings and an oversized shirt or off-the-shoulder blouse for an on-trend look. Comfortable platform with a non-skid, much lighter than you imagine, manmade sole.</p><p><strong>Features:</strong></p><ul><li>Leather oxford with brogue detail</li><li>Espadrille-inspired platform</li><li>1.25 inch heel</li><li>Round Toe</li><li>Adjustable lace-up closure</li><li>Water resistant</li><li>Leather upper</li><li>Antiskid manmade sole</li></ul>', '<p>A mixed-media oxford steals the scene with an espadrille-trimmed platform sole and dramatic brogue detailing on the burnished leather upper.</p>', '', 'A mixed-media oxford steals the scene with an espadrille-trimmed platform sole and dramatic brogue detailing on the burnished leather upper.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (5, 5, '', 'Grey Knitted Trainers', '<p>This shoe hits the sweet spot of effortless comfort and universal style. Knitted from a lightweight sports-mesh that hugs the foot for a snug fit, with an easy-entry collar shaped to perfection cushioned with the lightweight EVA midsole and finished with a flexible rubber outsole for excellent grip in any conditions, this shoe has delighted many a runner who just want a shoe that feels indulgent on their foot.&nbsp;</p><p><strong>Features:</strong></p><ul><li>Regular Fit</li><li>Knit textile upper</li><li>EVA midsole</li><li>Flexible rubber outsole</li></ul>', '<p>Designed with your comfort in mind, a pair of knitted trainers is a go to for any day! &nbsp;</p>', '', 'Designed with your comfort in mind, a pair of knitted trainers is a go to for any day! &nbsp;', '', 'en');
INSERT INTO `vs_product_translations` VALUES (6, 6, '', 'Lace-Up Boots in Light Brown', '<p>Waterproof, rugged, built for comfort and traction, these boots can go all day and will only look better with age.&nbsp;</p><p><strong>Features:</strong></p><ul><li>Premium leather upper</li><li>Seam-sealed waterproof construction</li><li>400 gram synthetic insulation</li><li>Padded collar</li><li>Leather lining</li><li>Flexible forefoot construction</li><li>Anti-fatigue midsole and footbed</li><li>Rubber lug outsole</li></ul>', '<p>Insulated waterproof boots for your next adventure.</p>', '', 'Insulated waterproof boots for your next adventure.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (7, 7, '', 'Boyfriend T-Shirt with Chest Logo', '<p>A wardrobe must-have, this crewneck tee features a comfortable cotton blend and a straight hem for easy, everyday wear. Think baggy and casual. Wear it again and again.</p>', '', '', 'A wardrobe must-have, this crewneck tee features a comfortable cotton blend and a straight hem for easy, everyday wear. Think baggy and casual. Wear it again and again.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (8, 8, '', 'Pink Sneakers with White Soles', '<p>Low-top silhouette with a durable canvas upper. &nbsp;Lace-up front with flat cotton laces. Round toe. Lightly padded tongue and collar. Vulcanized sole with high-wall foxings. Premium cushioned flexible sole. &nbsp;</p><p><strong>Features:</strong></p><ul><li>Regular Fit</li><li>Lace-up construction</li><li>Textile upper</li><li>Flexible rubber outsole</li></ul>', '<p>Timeless low top sneakers in pink. &nbsp;</p>', '', 'Timeless low top sneakers in pink. &nbsp;', '', 'en');
INSERT INTO `vs_product_translations` VALUES (9, 9, '', 'White Lace-Up Mesh Trainers', '<p>This pair is perfect for a variety of workouts in and out of the gym. School, play, travel, jogging, running - you name it! A great gift for a sporty girl.</p><p><strong>Features:</strong></p><ul><li>Lightweight</li><li>Mesh textile upper</li><li>Flexible shock absorbing EVA midsole</li><li>Rugged and durable outsole for long lasting traction</li></ul>', '<p>Breathable soft knit mesh sneakers for sports and more.</p>', '', 'Breathable soft knit mesh sneakers for sports and more.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (10, 10, '', 'Caged Cutout Sandals in Gold', '<p>These sandals feature an exquisite cut-out design on the upper and high heels to showcase your unique sense of style. This time made with a gold finish to give this piece a sleek and refined look. &nbsp;</p><p><strong>Features:</strong></p><ul><li>Vegan-friendly leather with geometric cutout design</li><li>Adjustable buckle closure at ankle</li><li>Open toe</li><li>Stiletto heel</li><li>Heel height: 3 <sup>1</sup>&frasl;<sub>2</sub> in</li></ul>', '<p>More feminine, more refined, more fun!</p>', '', 'More feminine, more refined, more fun!', '', 'en');
INSERT INTO `vs_product_translations` VALUES (11, 11, '', 'Green Flat Slingback Sandals', '<p>Quality made with premium elastic fabric, these strappy open toe sandals are a HOT TREND! Designed for all day comfort, they keep you moving while looking effortlessly stylish.&nbsp;</p><p><strong>Features:</strong></p><ul><li>Open toe sandal</li><li>Medium Width</li><li>Lightweight&nbsp;</li><li>Textile and synthetic leather upper</li><li>Soft rubber sole</li></ul>', '<p>With the rubber sole and wide elastic straps hugging your foot, these open toe sandals are made for walking.</p>', '', 'With the rubber sole and wide elastic straps hugging your foot, these open toe sandals are made for walking.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (12, 12, '', 'Waist Tie Dress in Blue and White Stripe', '<p>Crafted in a lightweight blend of linen and cotton and finished with muted stripes, this dress is an everyday piece that is perfect for long, sunny days.</p>', '', '', 'Crafted in a lightweight blend of linen and cotton and finished with muted stripes, this dress is an everyday piece that is perfect for long, sunny days.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (13, 13, '', 'Ultimate T-Shirt with Crew Neck in Red', '<p><br></p><ul><li>Crew neck</li><li>Short sleeves</li><li>Regular fit</li><li>100% Cotton</li></ul>', '<p>Another stylish addition your wardrobe. This crew-neck tee was designed specifically for a fashionable, comfortable, feminine look.</p>', '', 'Another stylish addition your wardrobe. This crew-neck tee was designed specifically for a fashionable, comfortable, feminine look.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (14, 14, '', 'Multicolored Dress with Balloon Sleeves [Options & Attributes]', '<ul><li>Round neck</li><li>Dropped shoulders</li><li>Balloon sleeves</li><li>Don&#39;t worry, you won&rsquo;t float away</li><li>Fitted cuffs&nbsp;</li><li>Relaxed fit</li><li>Loose but not oversized</li><li>100% Polyester</li></ul>', '<p>Cute, right? Bright floral print in relaxed fit. Throw on a black leather jacket and wrap a belt around the waist &nbsp;and you&#39;re ready for a night out.</p>', '', 'Cute, right? Bright floral print in relaxed fit. Throw on a black leather jacket and wrap a belt around the waist &nbsp;and you&#39;re ready for a night out.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (15, 15, '', 'Tall Pleated Midi Skirt in Check', '<ul><li>High rise</li><li>Just like your standards</li><li>Pleated design</li><li>Regular cut</li><li>Fits you just right</li><li>Lightweight knit. The kind that doesn&#39;t stretch</li><li>100% Polyester</li></ul>', '<p>Suitable for spring, summer and autumn. You can&#39;t miss such a charming skirt in your wardrobe.<br>Besides, you can dress it up with heels, or dress it down with sandals and t-shirt for a more casual look.</p><p><br></p><p><br></p>', '', 'Suitable for spring, summer and autumn. You can&#39;t miss such a charming skirt in your wardrobe. Besides, you can dress it up with heels, or dress it down with sandals and t-shirt for a more casual look.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (16, 16, '', 'Sleeved Mini Dress in Color Block', '<ul><li>Color-block design</li><li>55% Polyester, 45% Cotton</li><li>Round neck</li><li>Shift design</li><li>Regular cut</li><li>Fits you just right</li></ul>', '<p>An easy way to wear more color - 3-in-1 mini dress in modern color block design. Sleeves are non-detachable and can be completely unzipped to show that extra skin.&nbsp;</p>', '', 'An easy way to wear more color - 3-in-1 mini dress in modern color block design. Sleeves are non-detachable and can be completely unzipped to show that extra skin.&nbsp;', '', 'en');
INSERT INTO `vs_product_translations` VALUES (17, 17, '', 'Tall Slim Fit T-Shirt in White', '<ul><li>Throw on and go</li><li>Crew neck</li><li>Short sleeves</li><li>Plain design</li><li>Slim fit</li><li>100% Cotton.</li></ul>', '<p>This classic tee features all-cotton construction and a tag-free crew neckline for comfort. Affordable, high-quality, and long-lasting everyday clothing you can rely on.</p>', '', 'This classic tee features all-cotton construction and a tag-free crew neckline for comfort. Affordable, high-quality, and long-lasting everyday clothing you can rely on.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (18, 18, '', 'Skinny Western Denim Jacket in Light Blue Wash', '<p>The original jean jacket since 1962, this denim trucker jacket is fit for anything. This classic trucker features a standard regular fit with a hem that hits below the waist and welt hand pockets.</p>', '', '', 'The original jean jacket since 1962, this denim trucker jacket is fit for anything. This classic trucker features a standard regular fit with a hem that hits below the waist and welt hand pockets.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (19, 19, '', 'Oversized Hoodie in Grey', '<p>An oversized hoodie in grey for your chill days. &nbsp;More style for your money, with drawstring hood, fitted trims and dropped shoulders. Part of the Eco Edit. </p>', '', '', 'An oversized hoodie in grey for your chill days. &nbsp;More style for your money, with drawstring hood, fitted trims and dropped shoulders. Part of the Eco Edit.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (20, 20, '', 'Hooded Anorak Jacket in Black', '<p>This wind- and water-resistant jacket is a versatile, all-season piece with an impeccable fit built to keep you protected in milder weather and layer under shells and jackets for bonus warmth in more severe conditions.</p>', '', '', 'This wind- and water-resistant jacket is a versatile, all-season piece with an impeccable fit built to keep you protected in milder weather and layer under shells and jackets for bonus warmth in more severe conditions.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (21, 21, '', 'Leather Mini Tote Bag with Rose and Girl Print in Red', '<p><br></p><ul><li>Grab handle</li><li>Detachable straps</li><li>Flap-top closure</li><li>Avoid contact with liquids</li></ul>', '', '', 'Grab handle Detachable straps Flap-top closure Avoid contact with liquids', '', 'en');
INSERT INTO `vs_product_translations` VALUES (22, 22, '', 'Mesh Strap Watch in Gold', '<ul><li>Stainless-steel mesh strap</li><li>Gold-tone case</li><li>Three hand movement</li><li>Dash indices</li><li>Single crown to side</li><li>Presented in a branded gift-box</li></ul>', '<p>Elegant, flawlessly feminine and easy to read with a large polished goldtone dial. Let your wrist do the talking.</p>', '', 'Elegant, flawlessly feminine and easy to read with a large polished goldtone dial. Let your wrist do the talking.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (23, 23, '', 'Square Glasses in Black with Clear Lens', '<p>Frame: 100% Plastic, Lens: 100% Plastic.</p>', '<p>Whether you are looking for reading glasses for the office or home. These reading glasses will provide you with the best service and the premium protection you are looking for.</p><p>Square frames. Moulded nose pads for added comfort. Clear lenses. Tapered arms with curved temple tips for a secure fit.&nbsp;</p><p>Cloth sunglasses pouch can be used as a lens wipe.</p><p><br></p>', '', 'Whether you are looking for reading glasses for the office or home. These reading glasses will provide you with the best service and the premium protection you are looking for. Square frames. Moulded nose pads for added comfort. Clear lenses. Tapered arms with curved temple tips for a secure fit.&nbsp; Cloth sunglasses pouch can be used as a lens wipe.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (24, 24, '', 'Chain Drop Earrings in Gold Tone', '<p><br></p><ul><li>Drop design</li><li>Tiered hoops</li><li>Connecting chains</li><li>All earrings are non-returnable</li></ul>', '<p>For all-day-everyday use. Just give them a wipe to clean.</p>', '', 'For all-day-everyday use. Just give them a wipe to clean.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (25, 25, '', 'Rooster Mug in White [Related products]', '<p>For your AM coffee and PM tea break. Regular size. Silver-tone trims.</p>', '', '', 'For your AM coffee and PM tea break. Regular size. Silver-tone trims.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (26, 26, '', 'Festival Braslet Pack in Gold Tone', '<p>We know you like options. With the combination of woven design and polished shiny surface, this bracelet is just the thing to make any woman feel special. An elegant and generous visual enjoyment for you and the ones you love.</p>', '', '', 'We know you like options. With the combination of woven design and polished shiny surface, this bracelet is just the thing to make any woman feel special. An elegant and generous visual enjoyment for you and the ones you love.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (27, 27, '', 'Samsung - Galaxy S6 Edge Plus 4g LTE with 32gb Memory Cell Phone', '<ul><li>Wireless Compatibility - Wireless A|Wireless G|Wireless N|Wireless AC|Wireless B</li><li>Screen Size - 5.7 inches</li><li>Android 5.1.1 Lollipop</li><li>Internal Memory - 32 gigabytes</li></ul>', '', '', 'Wireless Compatibility - Wireless A|Wireless G|Wireless N|Wireless AC|Wireless B Screen Size - 5.7 inches Android 5.1.1 Lollipop Internal Memory - 32 gigabytes', '', 'en');
INSERT INTO `vs_product_translations` VALUES (28, 28, '', 'Apple iPhone 8 Plus, 64GB', '<div data-cel-widget=\"productDescription_feature_div\" data-feature-name=\"productDescription\"><div data-cel-widget=\"productDescription_feature_div\" data-feature-name=\"productDescription\" data-template-name=\"productDescription\"><br></div></div><ul><li>5.5-inch (diagonal) widescreen LCD Multi-Touch display with IPS technology - 1920-by-1080-pixel resolution at 401 ppiA11 Bionic chip with 64-bit architecture, Neural engine, Embedded M11 motion coprocessor</li><li>12MP wide-angle and telephoto camera, 7MP FaceTime HD Camera</li><li>Splash, water, and dust resistant</li></ul>', '<p>Designed with most durable glass and a stronger aerospace-grade aluminum band. Charges wirelessly. Resists water and dust. 5.5-inch Retina HD display with True Tone. 12MP dual cameras offer improved Portrait mode and new Portrait Lighting. Powered by A11 Bionic, a most powerful smartphone chip.</p>', '', 'Designed with most durable glass and a stronger aerospace-grade aluminum band. Charges wirelessly. Resists water and dust. 5.5-inch Retina HD display with True Tone. 12MP dual cameras offer improved Portrait mode and new Portrait Lighting. Powered by A11 Bionic, a most powerful smartphone chip.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (29, 29, '', 'Apple iPhone X [Options & Attributes] [Custom tabs]', '<ul><li>An all-new 5.8-inch Super Retina screen with all-screen OLED Multi-Touch display</li><li>12MP wide-angle and telephoto cameras with Dual optical image stabilization</li><li>Wireless Qi charging</li></ul>', '<div data-cel-widget=\"productDescription_feature_div\" data-feature-name=\"productDescription\"><div data-cel-widget=\"productDescription_feature_div\" data-feature-name=\"productDescription\" data-template-name=\"productDescription\"><p>iPhone that is entirely screen. One so immersive the device itself disappears into the experience. And so intelligent it can respond to a tap, your voice, and even a glance. With iPhone X, that vision is now a reality. Say hello to the future.</p></div></div><div data-cel-widget=\"renewedProgramDescriptionBtf_feature_div\" data-feature-name=\"renewedProgramDescriptionBtf\"><br></div>', '', 'iPhone that is entirely screen. One so immersive the device itself disappears into the experience. And so intelligent it can respond to a tap, your voice, and even a glance. With iPhone X, that vision is now a reality. Say hello to the future.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (30, 30, '', 'Apple Watch Series 3 (GPS, 38mm)', '<table><tbody><tr><td style=\"width: 12.5352%;\"><p>Connectivity</p></td><td style=\"width: 87.3944%;\"><p>Wi-Fi (802.11b/g/n 2.4GHz), Bluetooth 4.2</p></td></tr><tr><td style=\"width: 12.5352%;\"><p>Power</p></td><td style=\"width: 87.3944%;\"><p>Up to 18 hours of battery life</p></td></tr><tr><td style=\"width: 12.5352%;\"><p>Width</p></td><td style=\"width: 87.3944%;\"><p>33.3mm</p></td></tr><tr><td style=\"width: 12.5352%;\"><p>Depth</p></td><td style=\"width: 87.3944%;\"><p>11.4mm</p></td></tr><tr><td style=\"width: 12.5352%;\"><p>Case Weight</p></td><td style=\"width: 87.3944%;\"><p>36.7g</p></td></tr></tbody></table><table><tbody><tr><td><p>Case Size</p></td><td><p>38mm, 42mm</p></td></tr><tr><td><p>Features</p></td><td><p>GLONASS, Galileo, and QZSS, Barometric altimeter, Optical heart sensor, Accelerometer and gyroscope, Ion-X strengthened glass, Composite back</p></td></tr><tr><td><p>Whats in the box</p></td><td><p>Case, Band (either S/M or M/L length), 1m Magnetic Charging Cable</p></td></tr></tbody></table><p><br></p><p><br></p><p><br></p>', '<ul><li>GPS</li><li>Optical heart sensor</li><li>Digital Crown</li><li>S3 with dual-core processor</li><li>Accelerometer and gyroscope</li><li>Swim proof</li><li>watchOS 5</li><li>Aluminum case</li></ul>', '', 'GPS Optical heart sensor Digital Crown S3 with dual-core processor Accelerometer and gyroscope Swim proof watchOS 5 Aluminum case', '', 'en');
INSERT INTO `vs_product_translations` VALUES (31, 31, '', 'Google Home Mini [Attributes & Options]', '<p>A new Google smart speaker in mini format that shrinks down all the intelligence into a cheaper, smaller package.&nbsp;</p><p>A wifi-connected smart speaker that looks like a small fabric-covered pin cushion. It comes in a grey, black or coral with a colour-matched plastic base and rubber foot to keep it from sliding about when you prod it.</p>', '', '', 'A new Google smart speaker in mini format that shrinks down all the intelligence into a cheaper, smaller package.&nbsp; A wifi-connected smart speaker that looks like a small fabric-covered pin cushion. It comes in a grey, black or coral with a colour-matched plastic base and rubber foot to keep it from sliding about when you prod it.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (32, 32, '', 'Fitness Tracker Smart Watch', '<ul><li>Multifunction Smart Watch: The fitness tracker smart watch has data and 12/24H time display, Accurate heart rate, Blood pressure monitor, Sleep monitor, Auto pedometer, brightness adjustment, Weather, Clock alarm, Stopwatch, Sedentary reminder, Remote camera control, Music control, Find phone, Incoming call and SMS reminder, Message reminder etc.</li><li>Heart Rate Monitor &amp; Blood Pressure Fitness Tracker: Enter the watch face and long pressing the control area. Wait a moment about 30 seconds, then you can read the real time heart rate/blood pressure/blood oxygen data in the watch face. You can set the automatic heart rate monitor in the app. The history heart rate, blood pressure and blood oxygen details data are Available in the app.</li><li>IP67 Waterproof and Sleep tracking: The smart fitness tracker meets the international IP67 waterproof and dust proof standard. But we don&rsquo;t recommend to wear the fitness tracker while swimming or taking a shower; Comprehensively analyze data such as sleep duration, sleep depth and wakefulness sleep, guide you to improve sleep quality</li><li>See Calls &amp; Messages on Your Wrist: Receive call, SMS and SNS (Facebook, WhatsApp, LinkedIn, Instagram, and Twitter) notifications on display; Support system iOS 9.0 or above, Android 4.4 or above.(not for PC, iPad or Tablet).</li><li>Special Powerful Functions and Warranty:Weather forecast function, Music Player, Remote Camera Function, Change the Screen Brightness function and more other functions are available. 3-Months Warranty is Provided by Teamyo fitness tracker. Please feel free to contact us if you have any problem about our product, and we will response to you as soon as possible with full enthusiasm.</li></ul><p><strong>Warm Notes:</strong></p><p><strong>1. Compatibility:</strong> This fitness watch can be used with both Android and IOS system,Compatible with Android 4.4 or above, IOS 9 or above.Compatible with with bluetooth 4.0 or above.<br><strong>2. Waterproof Grade:</strong> IP67 Life Waterproof Grade. IP67 Waterproof resistant for sweat, rain, splash proof. Not recommend wear the fitness tracker when swimming or Take a shower.<br><strong>3. Charging cable:</strong> The smart watch is packed in a beautiful box. Please kindly find the charging cable under the box. Charge the fitness watch about 2 hours before firstly use.</p>', '<h2><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif; color: rgb(128, 128, 128);background-color: rgb(255, 255, 255);\">A modern waterproof a</span><span style=\"color: rgb(204, 204, 204);\"><span style=\"background-color: rgb(255, 255, 255);\"><a href=\"https://www.amazon.com/Activity-Waterproof-Pressure-Pedometer-V11-gray/dp/B07PNMRBJY/ref=sr_1_5?keywords=smart+watch&qid=1560875842&s=gateway&sr=8-5\"></a></span></span><span style=\"color: rgb(124, 112, 107);\"><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">ctivity tracker with heart rate monitor and blood pressure, step and calorie counter. Pedometer watch for women and men.&nbsp;</span></span><span style=\"color: rgb(204, 204, 204);\"><span style=\"background-color: rgb(255, 255, 255);\"><a href=\"https://www.amazon.com/Activity-Waterproof-Pressure-Pedometer-V11-gray/dp/B07PNMRBJY/ref=sr_1_5?keywords=smart+watch&qid=1560875842&s=gateway&sr=8-5\"></a></span></span></h2>', '', 'A modern waterproof a ctivity tracker with heart rate monitor and blood pressure, step and calorie counter. Pedometer watch for women and men.&nbsp;', '', 'en');
INSERT INTO `vs_product_translations` VALUES (33, 33, '', 'Bluetooth Smartwatch with Touch Screen and Fitness Tracker', '<p>The perfect companion to keep you organized and productive all day long. Receive one-way notifications for emails, SMS messages, and incoming phone calls (prompting you to take action on your smartphone), as well as alerts for Facebook, Twitter, WhatsApp, LinkedIn, Snap and other mobile apps.</p><p>It counts your steps, measures distance walked and estimate calories burned via a 3-axis accelerometer.<br>Monitor sleep quality and record sleep status data.</p><p>Rain and sweat water-resistant, can&#39;t be used for Hot Water washing and long time soak in water.</p><p>This wearable has an estimated 24-hour battery life. (Note: Battery life varies based on usage.)</p>', '', '', 'The perfect companion to keep you organized and productive all day long. Receive one-way notifications for emails, SMS messages, and incoming phone calls (prompting you to take action on your smartphone), as well as alerts for Facebook, Twitter, WhatsApp, LinkedIn, Snap and other mobile apps. It counts your steps, measures distance walked and estimate calories burned via a 3-axis accelerometer. Monitor sleep quality and record sleep status data. Rain and sweat water-resistant, can&#39;t be used for Hot Wat', '', 'en');
INSERT INTO `vs_product_translations` VALUES (34, 34, '', 'Aster i39 Headphones with Microphone', '<ul><li>Microphone, Foldabe, Stereo, Portable, Lightweight</li><li>Weight - 6.6 ounces</li><li>Dimentions - 8.3 x 7.1 x 3.5 in</li></ul>', '<p>Aster i39 on ear headphones are designed for ultimate comfort when you are travel, hang out, or on a business.</p><p>Take your headphones wherever you go. Just fold them up, twist up the cord, and be on your merry way</p>', '', 'Aster i39 on ear headphones are designed for ultimate comfort when you are travel, hang out, or on a business. Take your headphones wherever you go. Just fold them up, twist up the cord, and be on your merry way', '', 'en');
INSERT INTO `vs_product_translations` VALUES (35, 35, '', 'Apple AirPods', '<p>Unlike traditional, circular earbuds, the design of the EarPods is defined by the geometry of the ear which makes them more comfortable for more people than any other earbud-style headphones. &nbsp;The speakers inside the EarPods have been engineered to maximize sound output and minimize sound loss, which means you get high-quality audio.</p><p>Works with all devices that have a Lightning connector and support iOS 10 or later, including iPod touch, iPad, and iPhone.</p>', '', '', 'Unlike traditional, circular earbuds, the design of the EarPods is defined by the geometry of the ear which makes them more comfortable for more people than any other earbud-style headphones. &nbsp;The speakers inside the EarPods have been engineered to maximize sound output and minimize sound loss, which means you get high-quality audio. Works with all devices that have a Lightning connector and support iOS 10 or later, including iPod touch, iPad, and iPhone.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (36, 36, '', 'Cocooil Body Oil', '<p>Formulated with certified fair trade cold pressed organic coconut oil, produced sustainably in the Pacific Islands and supporting local communities. a Provides a luxurious feeling, perfect as your daily moisturiser all year long.</p><p>Ingredients: Helianthus Annuus (Sunflower) Seed Oil, Fair Trade Cold Pressed Organic Cocos Nucifera (Coconut) Oil, Vitas Vinifera (Grape) Seed Oil, Tocopherol.</p><p><strong>Features</strong>:</p><ul><li>Non-greasy body oil</li><li>Hydrating, nourishing and soothing &nbsp;<br><br></li></ul><p><br></p>', '<p>This body oil will nourish and protect your skin.</p>', '', 'This body oil will nourish and protect your skin.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (37, 37, '', 'Maui Moisture', '<p>With castor oil &amp; neem oil, this formula helps soften treated, natural, or transitioning hair.</p><p>Ingredients: Aloe Barbadensis Leaf Juice, Water (Aqua), Sodium C14-16 Olefin Sulfonate, Cocamidopropyl Betaine, PPG-2 Hydroxyethyl Coco/Isostearamide, Glycol Distearate, Cocos Nucifera (Coconut) Water, Bambusa Arundinacea Stem Extract, Ricinus Communis (Castor) Seed Oil, Melia Azadirachta Seed Oil, Chamomilla Recutita (Matricaria) Flower Extract, Polyquaternium-10, Cocamidopropyl Hydroxysultaine, Sodium Cocoyl Isethionate, PEG-120 Methyl Glucose Dioleate, Guar Hydroxypropyltrimonium Chloride, Methyl Gluceth-10, Tapioca Starch, Sodium Chloride, Propylene Glycol, Diazolidinyl Urea, Iodopropynyl Butylcarbamate, Fragrance (Parfum).&nbsp;</p><p><strong>Features</strong>:</p><ul><li>Fortifying</li><li>Encourages healthy-looking glow</li><li>Uses sulfate-free surfactants</li></ul>', '<p>A lush, strengthening shampoo that helps your hair feel rejuvenated &amp; restored.</p>', '', 'A lush, strengthening shampoo that helps your hair feel rejuvenated &amp; restored.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (38, 38, '', 'Barth Body Oil', '<p>Great for normal, sensitive and inflamed skin. Ideal for skin that needs to be calmed down and regenerated.&nbsp;</p><p><strong>Features</strong>:</p><ul><li>Penetrates the skin without leaving a greasy residue</li><li>Pleasant scent of exotic oils</li><li>Organic</li></ul>', '<p>With this amazing oil serum your skin will immediately feel very silky and hydrated.&nbsp;</p>', '', 'With this amazing oil serum your skin will immediately feel very silky and hydrated.&nbsp;', '', 'en');
INSERT INTO `vs_product_translations` VALUES (39, 39, '', 'Facial Spray', '<p>Created with aloe, cucumber and green tee, this spray hydrates, cools and refreshes. Mist over the face before moisturising or throughout the day after makeup, for a quick antioxidant boost.&nbsp;</p><p>Ingredients: Aqua (Water, Eau), Propylene Glycol, Mentha Piperita (Peppermint) Leaf Water, Cucumis Sativus (Cucumber) Fruit Water, Aloe Barbadensis Leaf Juice, Camellia Sinensis Leaf Extract, Cucumis Sativus (Cucumber) Fruit Extract, Fucus Vesiculosus Extract, Propanediol, Polysorbate 20, Ethylhexylglycerin, Benzophenone-4, Parfum (Fragrance), Sodium Chloride, Citric Acid, Potassium Sorbate, Sodium Benzoate, CI 19140 (Yellow 5), CI 42090 (Blue 1), Hexyl Cinnamal, Limonene, Linalool.&nbsp;</p><p><strong>Features</strong>:</p><ul><li>Calms and soothes skin</li><li>Antioxidant</li><li>Organic</li></ul>', '<p>A refreshing, hydrating mist for all skin types.</p>', '', 'A refreshing, hydrating mist for all skin types.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (40, 40, '', 'Red Dior', '<p>This limited edition stick delivers all the colour and shine of a lacquer, plus the comfort of a balm, with 6h* of wear and an exceptionally fine texture.&nbsp;</p><p><strong>Features</strong>:</p><ul><li>Liquified shine</li><li>Saturated lip color</li><li>Weightless wear</li></ul>', '<p>The new generation of lipstick: as easy to apply as it is to wear.</p>', '', 'The new generation of lipstick: as easy to apply as it is to wear.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (41, 41, '', 'Pink Unicorn Makeup Brushes', '<p>Crafted with the highest quality materials, each brush features whisper-soft synthetic bristles bundled with a gold ferrule. The sleek embellished holder keeps the tools organized in style.</p><p><strong>Features</strong>:</p><ul><li><strong>&nbsp;</strong>Includes 3 makeup brushes and a holder</li><li>Made with synthetic hairs; cruelty free.</li></ul>', '<p>Add some pink unicorn glam to your beauty routine! An essential collection of face and eye brushes for a perfected complexion and enchanting eye looks. &nbsp; &nbsp;</p>', '', 'Add some pink unicorn glam to your beauty routine! An essential collection of face and eye brushes for a perfected complexion and enchanting eye looks. &nbsp; &nbsp;', '', 'en');
INSERT INTO `vs_product_translations` VALUES (42, 42, '', 'Perfect Snacks', '<p>Made with freshly ground nut butters and organic honey, this snack bar is a delicious blend of creamy and crunchy with a cookie-dough-like texture. Each bar contains up to 17g of whole food protein.</p><p><strong>Features</strong>:</p><ul><li>A convenient source of vitamins and minerals</li><li>Non-GMO</li><li>Soy Free</li><li>Gluten Free</li><li>Organic</li><li>Kosher</li><li>Made in the USA</li></ul><p><br></p>', '<p>Nutritious protein for active lifestyles.</p>', '', 'Nutritious protein for active lifestyles.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (43, 43, '', 'Blue & Green Matcha', '<p>Pure organic matcha, blue and green. All natural weight loss, metabolism booster supplement. Delicious and nutricious. Enhances all day energy, mental clarity and focus. The best coffee substitute that does not give you jitters. Can be used in a variety of recipes for baking, lattes, smoothies, shakes, icecream and other foods.</p><p><strong>Features</strong>:</p><ul><li>Powerful antioxidant</li><li>Certified organic</li><li>Vegan</li><li>Non-GMO</li></ul>', '<h4><span style=\"font-size: 14px; color: rgb(128, 128, 128);\">Give matcha a try and be amazed.</span></h4><p><br></p><p><br></p>', '', 'Give matcha a try and be amazed.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (44, 44, '', 'Chia Seed', '<p>A rich source of protein, fiber, antioxidants, essential omega-3 fatty acids and minerals (magnesium, calcium, potassium, phosphorous). An ideal addition to vegetarian, vegan, whole food, paleo, ketogenic, and gluten-free diets. Can be consumed and digested whole. Add a healthful gourmet flair to salads, snacks, soups, and baked goods, or soak them for a satisfying pudding or gel.</p><p><strong>Features</strong>:</p><ul><li>Certified organic&nbsp;</li><li>Non-GMO&nbsp;</li><li>BPA-free packaging</li></ul>', '<p>Organic superfood.</p>', '', 'Organic superfood.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (45, 45, '', 'Probiotic and Omega 3, 6, 9 Supplement', '<p>Boost your energy, immune system and cognitive function by providing friendly bacteria for the digestive system and the Omega fatty acids for your brain. Each day, either morning or evening, take one capsule with a glass of water. Consistency is key.</p><p><strong>Features</strong>:</p><ul><li>Non-GMO</li><li>Antioxidant</li></ul>', '<p>A proprietary probiotic blend to support your brain and gut health.</p>', '', 'A proprietary probiotic blend to support your brain and gut health.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (46, 46, '', 'Tea Infused Sparkling Water', '<p>Sparkling water combined with organic tea and single-source, certified organic extracts. No &quot;natural flavors&quot;. &nbsp;</p><p><strong>Features</strong>:</p><ul><li>Unsweetened</li><li>Certified organic</li><li>Certified non-GMO</li></ul>', '<p>Tea and sparkling water: the perfect match!</p>', '', 'Tea and sparkling water: the perfect match!', '', 'en');
INSERT INTO `vs_product_translations` VALUES (47, 47, '', 'Cinnamon Sticks', '<h1><span style=\"font-size: 14px; color: rgb(128, 128, 128);\">Imported 100% raw from Sri Lanka.&nbsp;</span><span style=\"color: rgb(128, 128, 128); font-size: 14px;\">Perfect for baking, cooking and beverages. Hand rolled, soft and break or crumble easy.&nbsp;</span></h1><p><strong>Features</strong>:</p><ul><li>100+ sticks, packed and sealed for freshness&nbsp;</li><li>Non-GMO</li><li>Certfied organic</li><li>Kosher</li></ul>', '<p>Add some spice to your life with these finest quality pure Ceylon cinnamon sticks.</p>', '', 'Add some spice to your life with these finest quality pure Ceylon cinnamon sticks.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (48, 48, '', 'Peanut Butter', '<p>Made with just one ingredient: Dry-roasted organic peanuts.</p><ul><li>All natural&nbsp;</li><li>Non-GMO</li><li>Gluten free</li><li>Dairy free</li><li>Preservative free</li><li>Vegan</li><li>Kosher</li><li>No palm fruit oil</li><li>No added sugar</li><li>No added salt</li></ul>', '<p>Delicious peanut butter, all natural and loaded with protein.</p>', '', 'Delicious peanut butter, all natural and loaded with protein.', '', 'en');
INSERT INTO `vs_product_translations` VALUES (49, 49, '', 'Sweet Hazelnut and Chocolate Natural Flavouring', '<p>This natural food flavouring provides a classic hazelnut chocolate flavour to your cooking and baking, and can be used with various food and drink. Works great in general cooking/baking, cakes, yogurt, cheesecake, sorbets, drinks (including beer/cider), and ice-cream.&nbsp;</p><p>Ingredients:<strong>&nbsp;</strong>Natural Flavouring, Propylene Glycol (E1518).</p><p><strong>Features</strong>:</p><ul><li>Gluten free</li><li>Dairy free</li><li>Egg free</li><li>Vegan</li><li>Kosher</li></ul>', '<p><span style=\"font-size: 14px;\">Add the delicious taste of hazelnut chocolate to your cakes and other desserts using this natural food flavouring.&nbsp;</span></p>', '', 'Add the delicious taste of hazelnut chocolate to your cakes and other desserts using this natural food flavouring.&nbsp;', '', 'en');
INSERT INTO `vs_product_translations` VALUES (50, 50, '', 'Dose Juice', '<p>A complete array of vitamins, minerals and enzymes &ndash; all with the great taste. 60% green vegetables vs 40% fruits.&nbsp;</p><p>Features:</p><ul><li>Cold pressed</li><li>Organic</li></ul>', '<p>An organic juice to sustain you throughout the day. Drink it in the morning or between meals for a boost of energy and vitamins.&nbsp;</p>', '', 'An organic juice to sustain you throughout the day. Drink it in the morning or between meals for a boost of energy and vitamins.&nbsp;', '', 'en');
